mapper.select:
SELECT role.id, role.role_name, role.created_by, userInfo.user_account, role.created_time, role.updated_time, role.role_desc, role.status FROM role_info role LEFT JOIN user_info userInfo ON userInfo.id = role.created_by
role.delete_flag = 0
AND userInfo.user_account LIKE #{pattern}
AND role.role_name = #{roleName,jdbcType=VARCHAR}
AND role.created_time >= #{createdTimeStart,jdbcType=TIMESTAMP}
AND role.created_time <= #{createdTimeEnd,jdbcType=TIMESTAMP}

mapper.delete:
DELETE FROM role_app t
WHERE t.role_id = #{roleId,jdbcType=BIGINT}
AND t.app_id = #{appId,jdbcType=BIGINT}

DELETE FROM role_menu t
WHERE t.role_id = #{roleId,jdbcType=BIGINT}
AND t.menu_id = #{menuId,jdbcType=BIGINT}

DELETE FROM role_api t
WHERE t.role_id = #{roleId,jdbcType=BIGINT}
AND t.api_id = #{apiId,jdbcType=BIGINT}

DELETE FROM role_org t
WHERE t.role_id = #{roleId,jdbcType=BIGINT}
AND t.org_id = #{orgId,jdbcType=BIGINT}

DELETE FROM role_area t
WHERE t.role_id = #{roleId,jdbcType=BIGINT}
AND t.area_id = #{areaId,jdbcType=BIGINT}

DELETE FROM role_user t
WHERE t.role_id = #{roleId,jdbcType=BIGINT}
AND t.user_id = #{userId,jdbcType=BIGINT}

mapper.select:
SELECT app_id
FROM role_app
WHERE role_app.role_id = #{roleId,jdbcType=BIGINT}

SELECT menu_id
FROM role_menu
WHERE role_menu.role_id = #{roleId,jdbcType=BIGINT}

SELECT api_id
FROM role_api
WHERE role_api.role_id = #{roleId,jdbcType=BIGINT}

SELECT org_id
FROM role_org
WHERE role_org.role_id = #{roleId,jdbcType=BIGINT}

SELECT area_id
FROM role_area
WHERE role_area.role_id = #{roleId,jdbcType=BIGINT}

SELECT userInfo.id, userInfo.user_account, userInfo.user_name, userInfo.expiry_date_start, userInfo.expiry_date_end, userInfo.job_no, userInfo.address, userInfo.email, userInfo.phone, org.org_name_str org_name, GROUP_CONCAT( role.role_name ORDER BY userInfo.id SEPARATOR '/' ) role_name, userInfo.description FROM user_info userInfo LEFT JOIN role_user ru ON ru.user_id = userInfo.id LEFT JOIN role_info role ON role.id = ru.role_id LEFT JOIN org_info org ON org.id = userInfo.org_id WHERE user.delete_flag = 0
AND userInfo.user_account = #{userAccount,jdbcType=VARCHAR}
AND ru.role_id = #{roleId,jdbcType=BIGINT}
GROUP BY user.id ORDER BY user.id
