mapper.select:
SELECT role.id, role.role_name, role.created_by, userInfo.user_account, role.created_time, role.updated_time, role.role_desc, role.status FROM role_info role LEFT JOIN user_info userInfo ON userInfo.id = role.created_by
role.delete_flag = 0
AND userInfo.user_account LIKE #{patternAccount}
AND role.role_name LIKE #{patternRole}
AND role.created_time >= #{createdTimeStart,jdbcType=TIMESTAMP}
AND role.created_time <= #{createdTimeEnd,jdbcType=TIMESTAMP}
AND role.created_by = #{createdBy,jdbcType=BIGINT}
ORDER BY role.created_time DESC

WITH RECURSIVE org_info_str AS ( SELECT id, org_pid, org_name FROM org_info WHERE id = 1 UNION ALL SELECT va.id, va.org_pid, CONCAT( ap.org_name, '/', va.org_name ) AS org_name FROM org_info va INNER JOIN org_info_str ap ON va.org_pid = ap.id ) SELECT userInfo.id, userInfo.user_account, userInfo.user_name, userInfo.expiry_date_start, userInfo.expiry_date_end, userInfo.job_no, userInfo.address, userInfo.email, userInfo.phone, org.org_name, GROUP_CONCAT(role.role_name ORDER BY userInfo.id SEPARATOR '/') role_name, userInfo.description FROM user_info userInfo LEFT JOIN role_user ru ON ru.user_id = userInfo.id LEFT JOIN role_info role ON role.id = ru.role_id LEFT JOIN org_info_str org ON org.id = userInfo.org_id WHERE userInfo.delete_flag = 0
AND userInfo.user_account LIKE #{patternAccount}
AND ru.role_id = #{roleId,jdbcType=BIGINT}
GROUP BY userInfo.id, org.org_name ORDER BY userInfo.id
