mapper.select:
SELECT role.id, role.role_name, role.created_by, userInfo.user_account, role.created_time, role.updated_time, role.role_desc, role.status FROM sys_role_info role LEFT JOIN sys_user_info userInfo ON userInfo.id = role.created_by
role.delete_flag = 0
AND userInfo.user_account LIKE #{pattern}
AND role.role_name = #{roleName,jdbcType=VARCHAR}
AND role.created_time >= #{createdTimeStart,jdbcType=TIMESTAMP}
AND role.created_time <= #{createdTimeEnd,jdbcType=TIMESTAMP}

mapper.delete:
DELETE FROM sys_role_app t
WHERE t.role_id = #{roleId,jdbcType=BIGINT}
AND t.app_id = #{appId,jdbcType=BIGINT}

DELETE FROM sys_role_menu t
WHERE t.role_id = #{roleId,jdbcType=BIGINT}
AND t.menu_id = #{menuId,jdbcType=BIGINT}

DELETE FROM sys_role_api t
WHERE t.role_id = #{roleId,jdbcType=BIGINT}
AND t.api_id = #{apiId,jdbcType=BIGINT}

DELETE FROM sys_role_org t
WHERE t.role_id = #{roleId,jdbcType=BIGINT}
AND t.org_id = #{orgId,jdbcType=BIGINT}

DELETE FROM role_video_area t
WHERE t.role_id = #{roleId,jdbcType=BIGINT}
AND t.area_id = #{areaId,jdbcType=BIGINT}
