mapper.select:
SELECT t1.*, t2.name as device_name FROM `rundo_device_channel_expansion` t1 left join rundo_device_expansion t2 on t1.device_expansion_id=t2.id WHERE t1.deleted = 0 and t1.id in
#{id}
and t1.channel_name like concat('%',#{deviceChannelExpansionListReq.name},'%')
and t1.ip like concat('%',#{deviceChannelExpansionListReq.ip},'%')
and t1.ptz_type = #{deviceChannelExpansionListReq.ptzType}
and t1.online_state = #{deviceChannelExpansionListReq.onlineState}
order by t1.created_at,t1.online_state Desc

SELECT t1.id AS channelId, t1.channel_name, t1.online_state, t2.id AS deviceId, t2.name as device_name FROM `rundo_device_channel_expansion` t1 left join rundo_device_expansion t2 on t1.device_expansion_id=t2.id WHERE t1.deleted = 0 and t1.id in
#{id}
and t1.channel_name like concat('%',#{channelName},'%')
and t2.name like concat('%',#{deviceName},'%')
and t1.online_state = #{onlineState}
order by case when t1.id IN
#{id}
then 0 else 1 end, t1.online_state desc
order by t1.online_state Desc
